"use strict";document.addEventListener("DOMContentLoaded",()=>{if(document.querySelector(".burger")){const c=document.querySelector(".burger");c.addEventListener("click",function(){var e=document.querySelector(".header__menu"),t=document.querySelector(".header__auth");c.classList.toggle("active"),e.classList.toggle("active"),t.classList.toggle("active"),document.body.classList.toggle("lock")})}if(document.querySelector(".header")){var e=".header";const t=document.querySelector(e);window.onscroll=()=>{0<window.scrollY?t.classList.add("header--scroll"):t.classList.remove("header--scroll")}}});